import { Component } from '@angular/core';
import {AuthorizationService} from "../../service/authorization.service";
import {AuthorizationUtil} from "../../common/AuthorizationUtil";
import {Router} from "@angular/router";

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  constructor(public authorizationService: AuthorizationService,
              public router: Router) {
  }

  isLoggedIn(): boolean {
    return this.authorizationService.isLoggedIn();
  }

  getFirstName(): string {
    return AuthorizationUtil.getDecodedBearerToken().firstName;
  }

  redirectToLogin() {
    this.router.navigate(['login'])
  }
}
<div class="row">
  <div class="text-center">
    <p *ngIf="isLoggedIn()" class="mt-3 display-1">Welcome {{getFirstName()}}!</p>
    <div *ngIf="!isLoggedIn()" class="offset-3 col-6 d-grid gap-3">
      <p class="mt-3 display-1">Welcome!</p>
      <button type="button" class="btn btn-outline-success btn-block mt-5" (click)="redirectToLogin()">Log in</button>
    </div>
  </div>
</div>
